CREATE TABLE discipline (id BIGINT AUTO_INCREMENT, name VARCHAR(255) NOT NULL, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE match_table (id BIGINT AUTO_INCREMENT, starting_time DATETIME, closed TINYINT(1) DEFAULT '0' NOT NULL, played TINYINT(1) DEFAULT '0' NOT NULL, public TINYINT(1) DEFAULT '1' NOT NULL, discipline_id BIGINT NOT NULL, owner_id BIGINT, rental_id BIGINT, local_id BIGINT, away_id BIGINT, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, INDEX discipline_id_idx (discipline_id), INDEX owner_id_idx (owner_id), INDEX rental_id_idx (rental_id), INDEX local_id_idx (local_id), INDEX away_id_idx (away_id), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE match_player (match_id BIGINT, player_id BIGINT, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(match_id, player_id)) ENGINE = INNODB;
CREATE TABLE playing_field_rental (id BIGINT AUTO_INCREMENT, name VARCHAR(255) NOT NULL, address VARCHAR(255), city VARCHAR(255), province VARCHAR(255), country VARCHAR(255), phoneno VARCHAR(40), created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE playing_field_tenant (id BIGINT AUTO_INCREMENT, name VARCHAR(255) NOT NULL, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE rental_discipline (rental_id BIGINT, discipline_id BIGINT, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(rental_id, discipline_id)) ENGINE = INNODB;
CREATE TABLE team (id BIGINT AUTO_INCREMENT, name VARCHAR(255), discipline_id BIGINT NOT NULL, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, INDEX discipline_id_idx (discipline_id), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE team_player (team_id BIGINT, player_id BIGINT, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(team_id, player_id)) ENGINE = INNODB;
CREATE TABLE tenant_discipline (tenant_id BIGINT, discipline_id BIGINT, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(tenant_id, discipline_id)) ENGINE = INNODB;
CREATE TABLE sf_guard_forgot_password (id BIGINT AUTO_INCREMENT, user_id BIGINT NOT NULL, unique_key VARCHAR(255), expires_at DATETIME NOT NULL, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, INDEX user_id_idx (user_id), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE sf_guard_group (id BIGINT AUTO_INCREMENT, name VARCHAR(255) UNIQUE, description TEXT, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE sf_guard_group_permission (group_id BIGINT, permission_id BIGINT, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(group_id, permission_id)) ENGINE = INNODB;
CREATE TABLE sf_guard_permission (id BIGINT AUTO_INCREMENT, name VARCHAR(255) UNIQUE, description TEXT, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE sf_guard_remember_key (id BIGINT AUTO_INCREMENT, user_id BIGINT, remember_key VARCHAR(32), ip_address VARCHAR(50), created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, INDEX user_id_idx (user_id), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE sf_guard_user (id BIGINT AUTO_INCREMENT, first_name VARCHAR(255), last_name VARCHAR(255), email_address VARCHAR(255) NOT NULL UNIQUE, username VARCHAR(128) NOT NULL UNIQUE, algorithm VARCHAR(128) DEFAULT 'sha1' NOT NULL, salt VARCHAR(128), password VARCHAR(128), is_active TINYINT(1) DEFAULT '1', is_super_admin TINYINT(1) DEFAULT '0', last_login DATETIME, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, INDEX is_active_idx_idx (is_active), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE sf_guard_user_group (user_id BIGINT, group_id BIGINT, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(user_id, group_id)) ENGINE = INNODB;
CREATE TABLE sf_guard_user_permission (user_id BIGINT, permission_id BIGINT, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(user_id, permission_id)) ENGINE = INNODB;
CREATE TABLE sf_guard_user_profile (id BIGINT AUTO_INCREMENT, user_id BIGINT NOT NULL, nickname VARCHAR(32), first_name VARCHAR(30), last_name VARCHAR(30), birthday DATE, facebook_uid VARCHAR(20), email VARCHAR(255), email_hash VARCHAR(255), INDEX facebook_uid_index_idx (facebook_uid), INDEX email_index_idx (email), INDEX user_id_idx (user_id), PRIMARY KEY(id)) ENGINE = INNODB;
